<template>
    <b-container>
        <h2>Login</h2>
        <b-form @submit.prevent="handleSubmit">
            <b-form-group>
                <label for="username">Email</label>
                <b-form-input
                    type="text"
                    v-model="email"
                    name="email"
                    class="form-control"
                    :class="{ 'is-invalid': submitted && !email }"/>
                </b-form-input>
                <div v-show="submitted && !email" class="invalid-feedback">Email is required</div>
            </b-form-group>
            <b-form-group>
                <label htmlFor="password">Password</label>
                <b-form-input
                    type="password"
                    v-model="password"
                    name="password"
                    class="form-control"
                    :class="{ 'is-invalid': submitted && !password }">
                </b-form-input>
                <div v-show="submitted && !password" class="invalid-feedback">Password is required</div>
            </b-form-group>
            <b-form-group>
                <b-button variant="primary" :disabled="status.loggingIn">Login</b-button>
                <img v-show="status.loggingIn" src="data:image/gif;base64," />
                <b-link to="/register" class="btn btn-link">Register</b-link>
            </b-form-group>
        </b-form>
    </b-container>
</template>

<script>
import { mapState, mapActions } from 'vuex'

export default {
    data () {
        return {
            email: '',
            password: '',
            submitted: false
        }
    },
    computed: {
        ...mapState('account', ['status'])
    },
    created () {
        // reset login status
        this.logout();
    },
    methods: {
        ...mapActions('account', ['login', 'logout']),
        handleSubmit (e) {
            this.submitted = true;
            const { email, password } = this;
            if (email && password) {
                this.login({ username, password })
            }
        }
    }
};
</script>
